import { useRef } from 'react';

function App() {
  const ref = useRef();

  function genHexCode() {
    var possibleHex = ['1', '2', '3','4', '5', '6', '7', '8', '9', '0', 'A', 'B', 'C', 'D', 'E', 'F']
    var hexNumber = toString(Math.floor(Math.random() * possibleHex.length))

    var color = ''

    for (let i = 0; i <= 5; i++) {
      color = color + hexNumber
    }
    return color
  }

  function updateColor() {
    var newColor = '#FB9883'
    ref.current.style.backgroundColor = newColor;
    ref.current.style.color = 'white';
    console.log(genHexCode())
  };

  return (
    <div ref={ref}>
      <button onClick={updateColor}>
        Change the Background Color
      </button>
    </div>
  );
};

export default App;

