import { useState, useRef } from 'react';
import './App.css';

function App() {
  const ref = useRef()
  const [backgroundCol, setBackgroundCol] = useState('')

  function updateColor() {
    const newColor = '#FB9883'
    ref.current.style.backgroundCol = newColor
    // setBackgroundCol(newColor)
  }

  return (
     <div className="App" ref={ref}> 
      <form>
        <button onClick={updateColor}>Change the Background Color</button>
      </form>
    </div>
  );
}

export default App;


