//importing useRef
import { useRef } from 'react';


function App() {
  const ref = useRef();

  const updateColor = () => {
    ref.current.style.backgroundColor = 'black';
    ref.current.style.color = 'white';
  };

  return (
    <div ref={ref}>
      <button onClick={updateColor}>
        Change the Background Color
      </button>
    </div>
  );
};

export default App;

